```cpp
#include <string>
#include <iostream>

std::string parseMarkdownToHTML(std::string inputMarkdown) {
    // Replace all markdown special characters by their HTML counterparts
    for (auto &c : inputMarkdown) {
        switch (c) {
            case '`': c = '<code>'; break;
            case '*': c = '<strong>'; break;
            case '_': c = '<em>'; break;
            case '-': c = '<del>'; break;
            case '~': c = '<samp>'; break;
            case '>': c = '<h1>'; break;
            case '<': c = '<h2>'; break;
            case '?': c = '<h3>'; break;
            case '"': c = '<q>'; break;
            default: break;
        }
    }

    // Replace all markdown inline html elements by their equivalent HTML elements. For example, "code" is replaced by `<code>`, and "strong" is replaced by `<strong>`.
    for (auto &c : inputMarkdown) {
        switch (c) {
            case 'code': c = "<code>"; break;
            case 'strong': c = "<strong>"; break;
            case 'em': c = "<em>"; break;
            case 'del': c = "<del>"; break;
            case 'samp': c = "<samp>"; break;
            case 'h1': c = "<h1>"; break;
            case 'h2': c = "<h2>"; break;
            case 'h3': c = "<h3>"; break;
            case 'q': c = "<q>"; break;             // note: the above elements are replaced by their equivalent elements. However, the q element is not. Instead, it is replaced by a <blockquote> element. This is because <blockquote> is the equivalent to <q> in markdown. We can't replace <blockquote> by a <q> element because <q> doesn't exist in HTML. So instead, we replace <blockquote> by a <blockquote>.

