function calculateAvatarDimensions(cameraAngle, avatarRotation, avatarWidth, avatarHeight) {
  var avatarDimensions = {
    x: avatarWidth,
    y: avatarHeight,
    z: Math.sqrt(avatarWidth * avatarWidth + avatarHeight * avatarHeight)
  };
  var cosine = Math.cos(cameraAngle);
  var sinus = Math.sin(cameraAngle);
  var scale = avatarWidth / (2 * avatarDimensions.z);
  var xTranslation = (avatarWidth - (2 * scale) * scale) / 2;
  var yTranslation = (avatarHeight - (2 * scale) * scale) / 2;
  var zTranslation = -(scale / 2);
  var xRotation = avatarRotation - Math.atan(sinus / cosine);
  var yRotation = Math.atan(sinus / cosine) + xRotation;
  var zRotation = Math.atan(sinus / cosine);
  return { x: xTranslation, y: yTranslation, z: zTranslation, xRotation: xRotation, yRotation: yRotation, zRotation: zRotation };
}
```
