const slashCommand = new SlashCommandBuilder()
    .setName("ban")
    .setDescription("Ban someone from the server")
    .addStringOption(option => option.setName("user").setDescription("The user to ban").setRequired(true))
    .addStringOption(option => option.setName("reason").setDescription("Reason for the ban").setRequired(false))
    .addStringOption(option => option.setName("duration").setDescription("Duration of the ban in days").setRequired(false))
    .build();

bot.on("interactionCreate", interaction => {
    if (!interaction.isCommand()) return;
    if (interaction.commandName !== "ban") return;
    const { user, reason, duration } = interaction.options;
    const member = interaction.guild.members.get(user);
    if (!member) return interaction.reply({ content: "User not found" });
    const reasonEmbed = new Discord.MessageEmbed()
        .setAuthor(`${member}`, member.user.avatarURL)
        .setTitle(`Reason for Ban`)
        .setDescription(reason)
