```python
def middle_of_the_road(data, x, f, a, b):
    """Performs MMR on data using formula A(x) = f(b)-f(a) /(b-a) when x is input."""
    if not isinstance(data, list):
        raise TypeError("Data must be a list.")
    if len(data) < 2:
        raise ValueError("Data must contain at least 2 elements.")
    if not all(map(isinstance, (float, int))):
        raise TypeError("All elements of data must be numeric types.")
    if len(data) == 1:
        return data[0]
    try:
