function isPrime(array) {
    // Declaramos una variable booleana para almacenar si un número es primo
    let isPrime = false;
    // Recorremos el array, comenzando por el número 2
    for (let i = 2; i < array.length; i++) {
        // Comparamos cada número del array con el siguiente número del array
        if (array[i] % i === 0) {
            // Si el número anterior es divisible por el siguiente número, el número anterior no es primo
            isPrime = false;
            break;
        } else {
            // Si no se cumple la condición anterior, el número anterior es primo
            isPrime = true;
        }
    }
    // Devolvemos una función que imprima los números primos encontrados en el array, con un saltos de línea entre cada valor de la lista para ayudar con la visualización.
