#include <string>
#include <iostream>
#include <sstream>
#include <Pandoc/Pandoc.hpp>
#include <HTML5CPP/HTML5CPP.hpp>

std::string convertMarkdownToHTML(const std::string& markdownString) {
    std::istringstream input(markdownString);
    std::ostringstream output;
    Pandoc pandoc;
    pandoc.read(input);
    HTML5CPP::Document document;
    document.loadFromString(pandoc.convert());
    document.saveAsString(output);
    return output.str();
}
```
