import collections

class PriorityQueue(collections.MutableSequence):
    def __init__(self, iterable=None, max_heap=False):
        if iterable is not None:
            self.push(iterable)

    def push(self, x):
        if max_heap:
            self._push_max_heap(x)
        else:
            self._push_min_heap(x)

    def _push_max_heap(self, x):
        while len(self) > 1 and self[len(self)-1] <= x:
            self.pop()
        self.append(x)

    def _push_min_heap(self, x):
        while len(self) > 1 and self[0] >= x:
            self.pop()
        self.append(x)

    def pop(self): # popping from max heap
        x = self.pop() # remove last element from max heap stack and return it        
        while len(self) > 1 and self[len(self)-1] <= x: # push back smaller elements to max heap stack        
            self.append(x) # append larger elements to max heap stack        
        return x # return removed element        

    def __len__(self): # length of priority queue        
        return len(self._max_heap) + len(self._min_heap)        

    def __getitem__(self, i): # get element from priority queue        
        if i < len(self): # get from max heap        
            return self._max_heap[i]        
        else: # get from min heap        
            return self._min_heap[i - len(self)]        

    def __setitem__(self, i, x): # set element in priority queue        
        if i < len(self): # set in max heap        
            self._max_heap[i] = x        
        else: # set in min heap        
            self._min_heap[len(self) - i] = x
