#include <vector>
using namespace std;
const int N = 100000; // 输入大小限制
int dp[N+1]; // 动态规划数组
vector<int> dp_history; // 纪录数组
int n; // 要求的斐波那契数
int main() {
    cin >> n; // 输入要求的第n个斐波那契数
    dp_history.resize(N+1); // 初始化纪录数组长度为最大值N+1
    dp[0] = 1; // 初始化第一次计算到的斐波那契数为1
    for(int i = 1; i <= n; ++i) { // 从第i个斐波那契数开始进行计算
        int k = i - (i % n); // k表示需要处理下一次上边界和下边界之间（取模后）的那个位置上，其值取模n+1之后的余数（i % (n+1)），保持从0开始计算方式
        if(dp[k] != -1) { // 如果dp[k]不是-1表示已经处理过该元素，如果前k个元素在纪录数组中存在则会出现-1值
