def longestCommonSubsequence(s1, s2):
    """Returns the length of the longest common subsequence between two strings.

    Args:
        s1 (str): A string to be compared against s2.
        s2 (str): A string to be compared against s1.
    """
    # Create two lists for storing the lengths of matching substrings in each string.
    dp = [[0] * len(s2) for _ in range(len(s1) + 1)]

    # Initialize the first element of each list to 0.
    for i in range(len(s1) + 1):
        dp[i][0] = i

    # For each element of s2, update the length of its matching substrings in both strings.
    for i in range(1, len(s2) + 1):
        for j in range(1, len(s1) + 1):
