def find_longest_common_subsequence(s1, s2):
    n = len(s1)
    m = len(s2)
    dp = [[[0 for _ in range(m + 1)] for _ in range(n + 1)] for _ in range(2)] # 2D dp 表
    for i in range(1, m + 1):
        for j in range(1, n + 1):
