```javascript
const Discord = require('discord.js');
const client = new Discord.Client();

client.on('ready', () => {
  console.log('Bot is ready!');
});

client.on('message', message => {
  if (message.content === '/ban') {
    if (!message.member.hasPermission('MANAGE_MESSAGES')) return;
    const banUser = message.mentions.users.first();
    if (!banUser) return message.reply('Please mention a user to ban.');
    message.guild.ban(banUser);
    message.reply('User successfully banned!');
  } else {
    return; // Don't process any commands without "/ban" prefix, so that only "/ban" will be processed by our command handler below
  }
});
```
