public class TriangularNumberGenerator {
    private static boolean isTriangular(int n) {
        if (n == 0 || n == 1) {
            return true;
        }
        int x = 0;
        while (x < n) {
            if (n % x == 0) {
                return false;
            }
            x++;
        }
        return true;
    }
