function validateUserRequest(userStory) {
    // Create an array of functions that represent the criteria for fulfillment
    const criteria = [
        // Criteria 1: check if the user has provided an address in their story
        function(userStory) {
            // If the user has provided an address, return true
            if (userStory.hasOwnProperty("address")) {
