  {file.lines.map (line => line.toInt)} : Vector[Int]

// Sort un `Vector` avec Timsort.
def timsort_vector(vector: Vector[Int], key: Int => Int): Unit = {
  var base: Int = 0
  var len: Int = vector.length
  var idx = base
  while idx < len do {
    // Initialise les variables pour l'itération.
    var min = idx - 1 // index of smallest value so far in the range [base, idx) (exclusive).
    var max = idx + 1 // index of largest value so far in the range [base, idx) (inclusive).
    var k: Int = key(vector(idx)) // key of the current value in the range [base, idx).
    while true do { // Repeatedly:
      while max >= min do { // Repeat until the range [min, max] contains only distinct values.
        if key(vector(max)) <= k then max -= 1 else break
      }
      while min < max do { // Repeat until the range [min, max] contains only distinct values.
