```
// Assume two arrays: a and b.
// Assume that both arrays are sorted.
// Return true if there are any common elements in the two arrays.
bool findCommonElements(int* a, int* b, int lengthA, int lengthB) {
    if (lengthA > lengthB) {
        int temp = lengthA;
        lengthA = lengthB;
        lengthB = temp;
    }
    for (int i = 0; i < lengthA; i++) {
        for (int j = 0; j < lengthB; j++) {
            if (a[i] == b[j]) {
                return true;
            }
        }
    }
    return false;
}
