from collections import deque
import random

class TS(object):
    def __init__(self, n=None):
        if n is None:
            self.n = deque()
        else:
            self.n = deque(range(n))
    def sort(self):
        while self.n:
            if len(self.n) > 1:
                self._partition(0, len(self.n) - 1)
            else:
                return self.n[0]
    def _partition(self, p, r):
        q = random.randint(p + 1, r) # partition point for swapping mid element with the largest element in the right side of the list 
        self._swap(p, q) # swap mid and largest element in the right side of the list to keep track of largest element in the left side of the list 
        while q <= r: #while loop to swap elements to the left of p with q until a smaller value is found 
            if self._less_than(q, p): #compare values of q with p 
