#include <iostream>
#include <cmath>
#include <cstdlib>

using namespace std;

int n = 1; // Start with the first Fibonacci number
int fib[50]; // Store the Fibonacci numbers

void fibo() {
    for (int i = 2; i <= n; i++) { // Loop from index 2 to n-1 to find the Fibonacci numbers.
        fib[i] = fib[i - 1] + fib[i - 2]; // Add the two previous numbers to find the next Fibonacci number.
    }
}

