def find_common_longest_subsequence(s1, s2):
    """Finds the longest common subsequence between two strings."""
    # Create a list to store the characters in the longest subsequence
    longest_subseq = []
    # Initialize two lists to store the indices of the substrings in the input strings
    indices1 = []
    indices2 = []
    # Loop through the characters in each string and compare them with each other
    for i in range(len(s1)):
        if s1[i] == s2[i]:
            # Add the current character to the longest subsequence if it matches and both strings contain it
