// Huvudfunktionen för binära sökning.
// Array arr är sorterad i fallande ordning.
// Elementet element är det element vi söker.
// Returnerar index i arr där element finns, eller -1 om det inte finns.
int binarySearch(int[] arr, int element) {
    int left = 0, right = arr.length - 1;

    while (left <= right) {
        int middle = left + (right - left) / 2;
        if (arr[middle] == element) {
            return middle;
        } else if (arr[middle] < element) {
            left = middle + 1;
        } else { // arr[middle] > element, så ...
            right = middle - 1;
        }
    }
    return -1; // Elementet finns inte.
}
