std::string parse_markdown_to_html(std::string markdown_text) {
    // Split the markdown text into an array of words.
    std::string words[200];
    std::size_t split_count = split(markdown_text.begin(), markdown_text.end(), ' ', words);
    if (split_count == 0) {
        throw std::invalid_argument("Markdown text could not be parsed.");
    }

