def longest_common_subsequence(s1, s2):
    """Returns the longest common subsequence of two strings s1 and s2."""
    n1 = len(s1)
    n2 = len(s2)
    dp = [[0 for _ in range(n1 + 1)] for _ in range(n2 + 1)]
    for i in range(1, n1 + 1):
        for j in range(1, n2 + 1):
            if s1[i - 1] == s2[j - 1]:
