class Queue(object):
    def __init__(self):
        self._items = []
        self._front = 0
        self._rear = 0
        self._size = 0

    def enqueue(self, item):
        if self._front >= len(self._items):
            self._items.append(item)
            self._rear = len(self._items) - 1
            self._size += 1
        else:
            self._items.insert(self._front, item)
            self._front += 1

    def dequeue(self):
        if not self.is_empty():
            res = self._items[self._rear]
            self._rear -= 1
            del self._items[self._rear]
            self._size -= 1
            return res
