```java
public class IsDialógico {
    public static boolean isDialógico(int n) {
        // Algoritmo para determinar si una secuencia de enteros es dialógica
        // Entrada: n - entero a analizar
        // Salida: true si la secuencia es dialógica, false en caso contrario
        int x = 0;
        while (n != 1) {
            if (n % 3 == 0) {
                // Si la secuencia es par y divide por 3, significa que es dialógica
                return true;
            } else if (n % 3 == 1) {
                // Si la secuencia es impar y no divide por 3, significa que no es dialógica
                return false;
            } else {
                n = x;
                x = x + 1;
            }
        }
    }
}
