def get_longest_common_sequence(s1, s2):
    if len(s1) == len(s2):
        return s1
    if len(s1) < len(s2):
        s2 = s2[len(s1):]

    while len(s1) < len(s2):
        if s1[-1] == s2[0]:
            return s1 + s2[1:]
        s1 = s1[1:]
    return s1 + s2[len(s1):]
