from math import log

def social_graph_between(n, m, nodes=None):
    # nodes is a dictionary of dictionary, node[i] should be a list of strings (name)
    if nodes is None:
        nodes = {}
        for i in range(n):
            nodes[i] = [str(i)]
    else:
        for key, value in nodes.items():
            if len(value) != n:
                nodes[key] = [str(i) for i in range(n)]
    # set up the adjacency matrix A and the corresponding matrix A' (transpose)
    A = [[0 for _ in range(n)] for _ in range(n)]
    A_t = [[0 for _ in range(n)] for _ in range(n)]
    # loop through the rows of the adjacency matrix to fill them in
    for i in range(m):
        row_sum = 0
        # check if the row index is already filled
        if A[i][0]: continue # skip it if it has been filled already, which will happen when the loop finishes
        for j in range(1, n): # check if i is adjacent to j by checking row i against column j of the adjacency matrix (A) and setting the value of row i to the corresponding value from column j (A[i][j])
            if A[i][j]: row_sum += A[i][j] # add up the values between i and j that are adjacent to each other
        A[i][0] = row_sum # set row i to be equal to sum of all adjacent values with j > 0 and < n
        A_t[i][0] = A[i][0] # set row i to be equal to sum of all adjacent values with j > 0 and < n (transpose)
    return nodes, A, A_t

