def fib(n):
    # 定义两个列表，存储前n个斐波那契数
    fib_list = [0, 1]
    # 重复递归函数，递归返回斐波那契数
    def recur(n, i, prev_i):
        if n == 0:
            return prev_i
        elif n == 1:
            return fib_list[i]
        else:
            # 更新列表值，并重新计算当前次递归函数的条件满足条件
            fib_list[i] = prev_i + fib_list[i - 1]
            return recur(n - 1, i + 1, fib_list[i])
    # 调用递归函数，返回第n个斐波那契数
    return recur(n, 0, fib_list[0])
