```cpp
#include <iostream>
#include <vector>

using namespace std;

int main() {
    // Crear un vector que contenga algunos números
    vector<int> v{ 1, 2, 3, 4 };
    // Normalizar el vector con el método estándar de C++
    for (int i = 0; i < v.size(); i++) {
        v[i] /= v.size();
    }
    // Imprimir el vector normalizado en pantalla
    for (int i = 0; i < v.size(); i++) {
        cout << v[i] << " ";
    }
    cout << endl;
}
```

