import java.util.Stack;

public class Solution {
    public boolean isPowerOfThree(int n) {
        if (n < 0) return false;
        Stack<Integer> stack = new Stack<>();
        while (n != 0) {
            if (n % 3 == 0) {
                stack.push(n / 3);
            } else {
                if (stack.isEmpty()) return false;
                n = stack.pop();
            }
        }
        return stack.empty();
    }
}
```
