#include <string>
#include <sstream>
#include <iostream>
#include <iomanip>

std::string convertMarkdownToHTML(const std::string& input) {
    std::stringstream ss;
    for (char& c : input) {
        switch (c) {
            case '*': ss << "<strong>" << std::setw(3) << c; break;
            case '_': ss << "<em>" << std::setw(3) << c; break;
            case '~': ss << "<span style='color:red'>" << std::setw(3) << c; break;
            default: ss << c; break;
        }
    }
    return ss.str();
}
