# Warteschlangen-Datenstruktur mit zwei Stapeln
class Queue(object):
    def __init__(self, *args):
        self.stack1 = args[0]
        self.stack2 = args[1]
        self.length = len(self.stack1) + len(self.stack2)

    def isEmpty(self):
        return self.length == 0

    def enqueue(self, value):
        if not self.isEmpty():
            self.stack2.append(value)
        else:
            self.stack1.append(value)
            self.length += 1

    def dequeue(self):
        if not self.isEmpty():
            value = self.stack1.pop() # entfernt den ersten Eintrag aus Stack 1
            self.length -= 1 # reduziert die Länge der Warteschlange um einen Eintrag (inkl. des entfernten Eintrags)
            return value # zurückgabe des entfernten Eintrags
        else: # falls die Warteschlange leer ist, entfernt der erste Eintrag aus Stack 2 und gibt den Eintrag zurück, ohne die Länge der Warteschlange zu reduzieren, da er kein Element der Warteschlange ist. 
