function test (x: unknown): void {
    if (x === undefined) {
        console.log('x is undefined');
    } else if (typeof x === 'number') {
        console.log('x is a number');
    } else if (x === true) {
        console.log('x is true');
    } else if (x === false) {
        console.log('x is false');
    } else if (x === null) {
        console.log('x is null');
    } else if (typeof x === 'string') {
        console.log('x is a string');
    } else {
        console.log('unexpected type');
    }
}
test(123);// output: x is a number
test(true);// output: x is true
test(false);// output: x is false
test(null);// output: x is null
test('hello world!');// output: x is a string
test({});// output: unexpected type
```

