// Сортирайте групите в списъка `data` по азбучен ред
def group_sort(data: &[&T], sort_fn: (T) -> bool) where T: Eq:
  for i in 0..data.len()-1:
    for j in i..data.len()-1:
      if sort_fn(data[i]) == sort_fn(data[j]):
        swap(data[i], data[j])
