client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;
  const command = interaction.commandName;
  if (command !== 'ban') return;
  const member = interaction.member;
  const channel = interaction.channel;
  const user = member.user;
  const args = interaction.options.getArray().map(arg => arg.value);
  if (args.length === 0) return interaction.reply('Please provide a reason for banning');
  const reason = args[0];
  try {
    await member.ban({ reason }); // Ban the user for the provided reason.
    await channel.send(`User ${user} has been banned from the ${channel} channel for ${reason}.`); // Send a message to the channel announcing the ban.
  } catch (err) { // Handle any errors that may occur during ban execution or message sending.
    console.error(err); // Log the error to the console for debugging purposes.
    await interaction.reply('An error occurred while trying to ban the user'); // Reply to the interaction with an error message.
  }
}); // End of slash command setup code

