def lcs(s1, s2):
    """Return the length of the longest common subsequence of s1 and s2."""
    n = len(s1)
    m = len(s2)
    dp = [[0] * (n + 1) for _ in range(m + 1)]
    for i in range(n + 1):
        dp[i][0] = i
