class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def is_empty(self):
        return self.stack1 == [] and self.stack2 == []

    def enqueue(self, value):
        if not self.is_empty():
            raise ValueError("Queue is full")
        self.stack1.append(value)

    def dequeue(self):
