def normalize_vector(vec: np.ndarray) -> np.ndarray:
    max_expected_rotation = np.zeros(len(vec))
    for i in range(len(vec)):
        if vec[i] == 0:
            continue
        expected_rotation = np.zeros(len(vec))
        for j in range(len(vec)):
            if j != i:
                expected_rotation += vec[j] * np.cross(vec[i], vec[j]) / (np.linalg.norm(vec[i]) * np.linalg.norm(vec[j]))
        max_expected_rotation[i] = expected_rotation.max()
    normalized_vector = np.array([np.linalg.norm(v) / max_expected_rotation for v in vec])
    return normalized_vector

