std::string convertMarkdownToHtml(const std::string& markdown) {
    std::regex link_re("^\\[(.*?)\\](.*?)$");
    std::stringstream ss;
    ss << markdown;
    std::smatch matches;
    if (std::regex_match(ss.str(), matches, link_re)) {
        std::string text = matches[1].str();
        std::string url = matches[2].str();
