```lean4
#include <algorithm>
#include <vector>
#include <iostream>

void sort(std::vector<int>& vec) {
    auto is_sorted = std::partial_sum(vec.begin(), vec.end(), std::plus<int>());
    auto pivot = *std::max_element(is_sorted.begin(), is_sorted.end());
    std::stable_sort(vec.begin(), vec.end(), [&pivot](auto a, auto b) { return a < pivot; });
}
```

