#include <string>
#include <sstream>
#include <iostream>

// Implementations file
std::string convertMarkdownToHtml(const std::string& input) {
    // Split input on spaces and store it in a temporary string
    std::stringstream temp;
    temp << input;

    // Convert each element of the string to lowercase
    for (auto c : temp.str()) {
        temp << c.tolower();
    }

    // Concatenate all elements of the string and return it as a new string
    return temp.str();
}
