```javascript
function findCommonElements(a, b) {
    let result = [];
    a.sort();
    b.sort();
    for (let i = 0; i < a.length; i++) {
        let curr = a[i];
        for (let j = 0; j < b.length; j++) {
            if (curr === b[j]) {
                result.push(curr);
                break;
            } else {
                curr = a[++i];
            }
        }
    }
    return result;
}
