```lean
import std.list as list;

def timsort<T>(A: list<T>): list<T> = {
  // Definim una variable per emmagatzemar el nombre del primer element i el nombre del
  // últim element. Ens permetrà d'anar iterant entre aquests dos punts i reordenar els
  // elements d'A a la sortida.
  let first = A.head; let last = A.tail;
  // Crea la sortida com un nou contingut de la mateixa mida que A.
  let sortida = new list<T>;
  // Recorre els elements d'A des del primer al últim, passant per cada element un a un.
  for x in A do {
    if x < first then { // Si el valor actual és inferior al primer element, afegim-lo a la sortida.
      sortida += x;
    } else if x > last then { // Si el valor actual és superior al últim element, afegim-lo a la sortida.
      sortida += x;
    } else if x < last then { // Si el valor actual està entre el primer i el darrer element, afegim-lo a la sortida.
      sortida += x;
    } else if first > last then { // Si el primer i el darrer elements són iguals, afegim-los a la sortida.
      sortida += first; first = last; last = x;
    } else { // El primer i el darrer elements estan en ordre, no cal fer res més.
      break; // Saltem en aquest punt per evitar reordenar els elements d'A ja que són en ordre.
    }
  }
  // Retornem la nova sortida de Timsort.
  return sortida;
}

