import Lean.Array as Array
def timsort (array : Array Lean.Array.IArray) : Array Lean.Array.IArray :=
  let n := Array.length array
  let len := Array.length array.to_list
  if len > 1 then do {
    let pivot := get pivots len n array
    if pivot == -1 then return array
    let partition := partition array pivot (n - 1)
    return timsort partition ++ timsort (Array.deleteAt pivot partition)
  } else return array
```

