import re

def longest_common_prefix(strings):
    """Return the longest common prefix of a sequence of strings."""
    if not strings:
        return ''
    else:
        common_prefix = strings[0][0]
        for string in strings[1:]:
            if len(common_prefix) + len(string[0]) > len(common_prefix):
                common_prefix = string[0]
            else:
                return common_prefix
    return common_prefix
