public class Solution {
    public boolean isPowerOfThree(int n) {
        if (n == 0) {
            return false;
        }
        if (n <= 0 || n > Integer.MAX_VALUE) {
            return false;
        }
        int x = n;
        while (x > 0) {
            if (x % 3 == 0) {
                return true;
            }
            x = x / 3;
        }
        return false;
