```javascript
// index.js
import "./styles.css";
import App from "./App";
import { AppRegex, AppNLP } from "./constants";
import { Context } from "@vercel/next";
import { isPWA, isNext, getNextJSContext } from "@vercel/next-dev-utils";

if (isNext()) {
    const context = getNextJSContext();
    const pwa = isPWA(context);
    const language = getLanguage(context);
    if (pwa && language === AppNLP) {
        // React JSX elements will replace app's HTML for this route only.
        return <AppRegex />;
    } else if (!pwa && language === AppNLP) {
        // Fallback to HTML content when PWA is disabled.
        return (<HTML lang={language} />);
    } else {
        // Any other route will render the default HTML.
        return <App />;
    }
} else {
    return <App />;
}
```

