```
#include <iostream>
#include <algorithm>
#include <vector>
#include <numeric>
#include <utility>
#include <chrono>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false); cin.tie(nullptr);
    int n; cin >> n;
    vector<int> arr(n);
    for (int i = 0; i < n; i++) { cin >> arr[i]; }
    sort(begin(arr), end(arr));
    int ans = accumulate(begin(arr), end(arr), 0);
    for (int i = n - 1; i >=0; i--) { if (arr[i] % n == i) { ans++; } }
    cout << ans << endl;
}
