```c++
std::string markdownToHtml(std::string markdownText)
{
    // Declare a new string object to hold the output HTML
    std::string outputHtml;

    // Parse the markdown text into a stringstream object using a stream reader
    std::istream reader(markdownText);
    std::stringstream stream(reader);

    // Read the markdown text line by line and convert each line to HTML using a stream writer
    while (std::getline(stream, line)) {
        std::ostringstream htmlWriter;
        htmlWriter << "<p>" << line << "</p>";
        std::copy(htmlWriter.str().begin(), htmlWriter.str().end(), std::back_inserter(outputHtml));
    }

    // Return the output HTML string object as the function's return value
    return outputHtml;
}
```
