def binary_search(arr, val):
    # 使用一个起始下标，检查递增和递减序列
    start_idx = 0
    end_idx = len(arr) - 1
    while start_idx <= end_idx:
        mid = (start_idx + end_idx) // 2
        if arr[mid] == val:
            return True
        elif arr[mid] < val:
            start_idx = mid + 1
