```lean
def timsort(a: List[i32]):
    def less(x, y):
        if x > y: return True
        if x < y: return False
        return x < y

    result = []
    last = None
    for x in a:
        if last is not None and less(last, x):
            result.append(x)
        else:
            last = x

    result.reverse()
    return result[:]

