std::string convertMarkdownToHtml(const std::string& markdownText) {
  // Replace markdown links with html links
  for (size_t i = 0; i < markdownText.length(); i++) {
    if (markdownText[i] == '[') {
      std::string link = markdownText.substr(i + 1, markdownText.length() - i - 2);
      if (link.find(']') != std::string::npos) {
        // Check if the link is followed by an open square bracket. If so, this is a link to an image.
        std::string imageLink = link.substr(0, link.find(']'));
        markdownText.replace(i, markdownText.length(), '<img src="' + imageLink + '">');
        i += imageLink.length() - 1; // This is the length of the image link, not the actual image size.
      } else {
        // This is not an image link, so just replace the square brackets with html links.
        markdownText.replace(i, markdownText.length(), '<a href="' + link + '">' + link + '</a>');
      }
    }
  }
  return markdownText;
}
```

